UPDATE 
	formacionacademica INNER JOIN 
	Titulado ON formacionacademica.idTitulado=Titulado.idTitulado INNER JOIN
	Municipio ON Titulado.idMunicipio=Municipio.idMunicipio
SET 
	idCentro=-(Municipio.idProvincia)
WHERE 
	formacionacademica.idCentro=-1;
